import React, { useState, useEffect } from 'react'
import './App.css'
import Nav from './Nav'
import Amplify, { API, graphqlOperation } from 'aws-amplify'
import awsExports from './aws-exports'
import { updateVisitorCount, createVisitorCount } from './graphql/mutations'
import { getVisitorCount } from './graphql/queries'

Amplify.configure(awsExports)

function App () {
  const [visitors, setVisitors] = useState(0)

  useEffect(() => {
    fetchAndUpdateVisitors()
  }, [])

  async function fetchAndUpdateVisitors () {
    try {
      const response = await API.graphql(
        graphqlOperation(getVisitorCount, { input: { id: "1"} })
      );
      const num = response.data.visitorCount.count;
      setVisitors(num + 1);
      await API.graphql(
        graphqlOperation(updateVisitorCount, { count: visitors })
      );
    } catch (err) {
      console.log(err)
    }
  }
  return (
    <div className='welcome-page'>
      <p className='index_title'>welcome to my artistic cloud space</p>
      <p className='visitor_dashboard'>
        you are visitor # {visitors} to my site!
      </p>
      <Nav />
    </div>
  )
}

export default App
